// Helper functions

func rdpStatusDotClass(status string) string {
	switch status {
	case "online", "active":
		return "bg-green-500"
	case "error", "failed":
		return "bg-red-500"
	default:
		return "bg-gray-500"
	}
}

func rdpStatusBadgeClass(status string) string {
	switch status {
	case "online", "active":
		return "bg-green-500/10 text-green-400"
	case "error", "failed":
		return "bg-red-500/10 text-red-400"
	default:
		return "bg-gray-500/10 text-gray-400"
	}
}

func formatRDPAddress(host string, port int) string {
	if port == 3389 || port == 0 {
		return host
	}
	return fmt.Sprintf("%s:%d", host, port)
}

func rdpSecurityLabel(security string) string {
	switch security {
	case "nla":
		return "NLA (Network Level Auth)"
	case "tls":
		return "TLS"
	case "rdp":
		return "RDP (Legacy)"
	default:
		return "Auto-negotiate"
	}
}

