func gradeClass(grade string) string {
	switch grade {
	case "A":
		return "bg-green-500/20 text-green-400"
	case "B":
		return "bg-blue-500/20 text-blue-400"
	case "C":
		return "bg-yellow-500/20 text-yellow-400"
	case "D":
		return "bg-orange-500/20 text-orange-400"
	default:
		return "bg-red-500/20 text-red-400"
	}
}

func stateColor(state string) string {
	switch state {
	case "running":
		return "bg-green-500"
	case "paused":
		return "bg-yellow-500"
	case "restarting":
		return "bg-blue-500"
	default:
		return "bg-red-500"
	}
}

func stateBadge(state string) string {
	switch state {
	case "running":
		return "bg-green-500/20 text-green-400"
	case "paused":
		return "bg-yellow-500/20 text-yellow-400"
	case "restarting":
		return "bg-blue-500/20 text-blue-400"
	default:
		return "bg-red-500/20 text-red-400"
	}
}

func eventTypeClass(action string) string {
	switch action {
	case "start", "create":
		return "bg-green-500/20 text-green-400"
	case "stop", "kill", "die", "destroy":
		return "bg-red-500/20 text-red-400"
	case "restart":
		return "bg-yellow-500/20 text-yellow-400"
	case "pull":
		return "bg-blue-500/20 text-blue-400"
	default:
		return "bg-gray-500/20 text-gray-400"
	}
}

func eventIcon(action string) string {
	switch action {
	case "start":
		return "fa-play"
	case "stop":
		return "fa-stop"
	case "kill", "die":
		return "fa-skull"
	case "create":
		return "fa-plus"
	case "destroy":
		return "fa-trash"
	case "restart":
		return "fa-redo"
	case "pull":
		return "fa-download"
	default:
		return "fa-info"
	}
}

