func alertColor(severity string) string {
	if severity == "critical" {
		return "text-red-400"
	}
	return "text-yellow-400"
}

func progressColor(percent float64) string {
	if percent >= 95 {
		return "bg-red-500"
	}
	if percent >= 80 {
		return "bg-yellow-500"
	}
	if percent >= 60 {
		return "bg-blue-500"
	}
	return "bg-green-500"
}

func usageColor(percent float64) string {
	if percent >= 95 {
		return "text-red-400"
	}
	if percent >= 80 {
		return "text-yellow-400"
	}
	return "text-gray-300"
}

func resourceIcon(resourceType string) string {
	switch resourceType {
	case "cpu":
		return "fas fa-microchip"
	case "memory":
		return "fas fa-memory"
	case "containers":
		return "fas fa-cube"
	case "images":
		return "fas fa-layer-group"
	case "volumes":
		return "fas fa-database"
	case "disk":
		return "fas fa-hdd"
	default:
		return "fas fa-tachometer-alt"
	}
}

func resourceIconBg(resourceType string) string {
	switch resourceType {
	case "cpu":
		return "bg-blue-500/20"
	case "memory":
		return "bg-purple-500/20"
	case "containers":
		return "bg-green-500/20"
	case "images":
		return "bg-cyan-500/20"
	case "volumes":
		return "bg-orange-500/20"
	case "disk":
		return "bg-red-500/20"
	default:
		return "bg-gray-500/20"
	}
}

func resourceIconColor(resourceType string) string {
	switch resourceType {
	case "cpu":
		return "text-blue-400"
	case "memory":
		return "text-purple-400"
	case "containers":
		return "text-green-400"
	case "images":
		return "text-cyan-400"
	case "volumes":
		return "text-orange-400"
	case "disk":
		return "text-red-400"
	default:
		return "text-gray-400"
	}
}

func min(a, b float64) float64 {
	if a < b {
		return a
	}
	return b
}

func max(a, b float64) float64 {
	if a > b {
		return a
	}
	return b
}

