func scoreBarColor(score int) string {
	switch {
	case score >= 80:
		return "bg-green-500"
	case score >= 60:
		return "bg-blue-500"
	case score >= 40:
		return "bg-yellow-500"
	case score >= 20:
		return "bg-orange-500"
	default:
		return "bg-red-500"
	}
}

