func countActive(captures []CaptureSession) int {
	count := 0
	for _, c := range captures {
		if c.Status == "running" {
			count++
		}
	}
	return count
}

func totalSize(captures []CaptureSession) string {
	if len(captures) == 0 {
		return "0 B"
	}
	var total int64
	for _, c := range captures {
		total += c.FileSize
	}
	if total == 0 {
		return "0 B"
	}
	const (
		kb int64 = 1024
		mb       = 1024 * kb
		gb       = 1024 * mb
	)
	switch {
	case total >= gb:
		return fmt.Sprintf("%.1f GB", float64(total)/float64(gb))
	case total >= mb:
		return fmt.Sprintf("%.1f MB", float64(total)/float64(mb))
	case total >= kb:
		return fmt.Sprintf("%.1f KB", float64(total)/float64(kb))
	default:
		return fmt.Sprintf("%d B", total)
	}
}

func ifaceStatusColor(status string) string {
	switch status {
	case "up":
		return "bg-green-400"
	case "down":
		return "bg-red-400"
	default:
		return "bg-gray-400"
	}
}

func captureStatusBadge(status string) string {
	switch status {
	case "running":
		return "bg-green-500/10 text-green-400"
	case "stopped":
		return "bg-yellow-500/10 text-yellow-400"
	case "completed":
		return "bg-blue-500/10 text-blue-400"
	default:
		return "bg-gray-500/10 text-gray-400"
	}
}

func captureStatusBg(status string) string {
	switch status {
	case "running":
		return "bg-green-500/10"
	case "stopped":
		return "bg-yellow-500/10"
	case "completed":
		return "bg-blue-500/10"
	default:
		return "bg-gray-500/10"
	}
}

func captureStatusIcon(status string) string {
	switch status {
	case "running":
		return "fas fa-circle text-green-400 animate-pulse"
	case "stopped":
		return "fas fa-stop text-yellow-400"
	case "completed":
		return "fas fa-check text-blue-400"
	default:
		return "fas fa-question text-gray-400"
	}
}

